      chromeExe = 'Chrome' + (platform === 'canary' ? ' Canary' : '');
      child_process.spawn('cmd', ['/s', '/c', 'start', chromeExe].concat(chromeArgs));
    } else if (process.platform === 'darwin') {
      chromeExe = 'Google Chrome' + (platform === 'canary' ? ' Canary' : '');
